(3794, 177, 'subtitle', '<strong>IXTERO</strong> — ведущий поставщик тротуарной плитки\r\nв Восточной Сибири, Республике Бурятия и Забайкальском крае!'),
(3795, 177, '_subtitle', 'field_64708316624b7'),
(3796, 177, 'about-img', '118'),
