(6848, 227, '_about-title', 'field_6470830a624b6'),
(6849, 227, 'subtitle', '<strong>IXTERO</strong> — ведущий поставщик тротуарной плитки\r\nв Восточной Сибири, Республике Бурятия и Забайкальском крае!'),
(6850, 227, '_subtitle', 'field_64708316624b7'),
(6851, 227, 'about-img', '118'),
